sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( isLoading || newValue == '') {
			return;
		}

		var Requesttype = g_form.getValue('u_request_type');

		// Clear all of the choices from the Request type field
		  // g_form.clearOptions('u_request_type');


			// If the the value of the What needed field is Human Resources 1 or Human Resources 2, add
		// one hr choice and other to the Request type field choice list
		if(newValue == 'hr1' || newValue == 'hr2'){

			g_form.setValue('u_request_type','Human Resources');


		}
		// If the the value of the Request type field is facilities, add
		// two facilities choices and other to the What needed field
		// choice list
		else if(newValue == 'facilities1' || newValue == 'facilities2'){
			g_form.setValue('u_request_type','Facilities');
		}
		// If the the value of the Request type field is legal, add
		// two legal choices and other to the What needed field
		// choice list
		else if(newValue == 'legal1' || newValue == 'legal2'){
			g_form.setValue('u_request_type','Legal');
		}
		else if(newValue == 'other'){
			g_form.setValue('u_request_type','Other');
		}

		// If the form is loading and it is not a new record, set the u_what_needed value to the
		// value from the record before it was loaded
		//if(isLoading && !g_form.isNewRecord()){
			//g_form.setValue('u_request_type', Requesttype);
		//}
	}
